  return true;
});

console.log(`ðŸ“‹ After natural conversation filter: ${naturalPosts.length} natural posts`);

// STRICT TOP 5 SELECTION - ALWAYS 5 POSTS
let selectedPosts = [];

if (naturalPosts.length >= 5) {
  // Calculate quality score for each natural post
  const scoredPosts = naturalPosts.map(post => {
    // Quality scoring criteria:
    // 1. Character count closer to 95 (ideal length)
