    const lengthScore = 100 - Math.abs(95 - post.character_count); // Higher = better
    const contentDirectnessScore = post.content.includes('comme') ? 15 : 0; // Bonus for analogies/metaphors
    const tagScore = post.tags.length >= 3 ? 10 : 0; // Bonus for good tagging

    // Comment quality score - favor natural, varied conversation
    const comment = post.comment.toLowerCase();
    let commentScore = 0;

    // Bonus for concrete situations without clichÃ©s
    if (comment.includes('exemple') || comment.includes('situation') || comment.includes('cas')) {
      commentScore += 15; // Bonus for concrete examples
    }

    // Bonus for natural personal references (but not clichÃ©d ones)
    if ((comment.includes('mon') || comment.includes('ma') || comment.includes('mes')) && 
        !comment.includes('mon pÃ¨re m\'a toujours dit')) {
      commentScore += 10; // Bonus for natural personal stories
    }

    // Bonus for actionable/practical content
    if (comment.includes('conseil') || comment.includes('astuce') || comment.includes('technique') || 
