const now = new Date();
const scheduledPosts = [];

// Helper function to get random hour between optimal ranges
function getRandomHour() {
  const morningHours = [7, 8, 9, 10];      // Morning engagement
  const lunchHours = [12, 13, 14];         // Lunch break
  const afternoonHours = [15, 16, 17];     // Afternoon break
  const eveningHours = [18, 19, 20, 21];   // Evening engagement

  const allHours = [...morningHours, ...lunchHours, ...afternoonHours, ...eveningHours];
  return allHours[Math.floor(Math.random() * allHours.length)];
}

// Helper function to get random future date (1-14 days = next 2 weeks)
function getRandomFutureDate() {
  const futureDate = new Date(now);
  // Extended range: 1-14 days (covers this week + next week)
  futureDate.setDate(now.getDate() + Math.floor(Math.random() * 14) + 1);
  return futureDate;
}

// Helper function to get date string (YYYY-MM-DD) for comparison
function getDateString(date) {
  return date.toISOString().split('T')[0];
}

// Helper function to extract theme/topic from post content
function extractTheme(post) {
  const content = post.content || '';
  const originalPrompt = inputData.original_prompt || '';

  // Use the original prompt as base theme
  let theme = originalPrompt.substring(0, 50).toLowerCase();

  // Extract key theme words from content for more specific theming
  const themeKeywords = [
    'amour', 'love', 'relationship',
    'motivation', 'success', 'rÃ©ussite',
    'argent', 'money', 'finance', 'business',
    'santÃ©', 'health', 'wellness',
    'famille', 'family', 'enfant',
    'travail', 'work', 'career', 'carriere',
    'bonheur', 'happiness', 'joie',
    'stress', 'anxiÃ©tÃ©', 'mental',
    'voyage', 'travel', 'aventure',
    'sport', 'fitness', 'exercise',
    'choix', 'decision', 'dÃ©cision'
  ];

  for (const keyword of themeKeywords) {
    if (content.toLowerCase().includes(keyword) || originalPrompt.toLowerCase().includes(keyword)) {
      theme = keyword;
      break;
    }
  }

  return theme;
}

// If no posts, return error
if (!posts || posts.length === 0) {
  console.log('âŒ No posts found after all attempts');
  console.log('Available input fields:', Object.keys(inputData));
  throw new Error('Aucun post trouvÃ© pour la programmation. DonnÃ©es reÃ§ues: ' + JSON.stringify(inputData, null, 2));
}

console.log('âœ… Processing', posts.length, 'posts for scheduling');

// Track used dates per theme to avoid conflicts
const usedDatesPerTheme = {};
const usedDatesGlobal = new Set();

posts.forEach((post, index) => {
  const theme = extractTheme(post);

  // Initialize theme tracking if needed
  if (!usedDatesPerTheme[theme]) {
    usedDatesPerTheme[theme] = new Set();
  }

  let postDate;
  let dateString;
  let attempts = 0;
  const maxAttempts = 50; // Prevent infinite loops

  // Find a date that doesn't conflict with the same theme
  do {
    postDate = getRandomFutureDate();
    dateString = getDateString(postDate);
    attempts++;

    // If we can't find a unique date for this theme, allow some overlap but spread it out
    if (attempts > maxAttempts) {
      console.log(`Warning: Could not find unique date for theme "${theme}" after ${maxAttempts} attempts`);
      break;
    }
  } while (usedDatesPerTheme[theme].has(dateString) && attempts < maxAttempts);

  // Set the time
  postDate.setHours(getRandomHour(), Math.floor(Math.random() * 60), 0, 0);

  // Mark this date as used for this theme
  usedDatesPerTheme[theme].add(dateString);
  usedDatesGlobal.add(dateString);

  // Generate unique ID
  const generateId = () => {
    return 'POST_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9) + '_' + index;
  };

  const scheduledPost = {
    id: generateId(),
    is_posted: false,
    posted_on: null,
    scheduled_for: postDate.toISOString(),
    content: post.content || '',
    comment: post.comment || '',
    tags: Array.isArray(post.tags) ? post.tags.join(',') : (post.tags || ''),
    media: null,
    to_post_on: 'Facebook',
    content_type: 'Background Post',
    engagement_score: null,
    created_by: inputData.user_id || 'unknown',
    created_at: now.toISOString(),
    last_updated: now.toISOString(),
    status: 'scheduled',
    topic: theme, // Use the extracted theme
    character_count: post.content ? post.content.length : 0,
    priority: Math.floor(Math.random() * 3) + 1,
    target_audience: 'General',
    call_to_action: 'Engage',
    post_category: 'Motivational',
    // Additional fields for better tracking
    theme: theme,
    original_prompt: inputData.original_prompt || '',
    scheduled_date: dateString,
    original_post_number: post.post_number || index + 1
  };

  scheduledPosts.push(scheduledPost);

  console.log(`ðŸ“… Post ${index + 1} - Theme: "${theme}" scheduled for ${dateString} at ${postDate.getHours()}:${postDate.getMinutes().toString().padStart(2, '0')}`);
});

// Sort posts by scheduled date for better organization
scheduledPosts.sort((a, b) => new Date(a.scheduled_for) - new Date(b.scheduled_for));

// Log scheduling summary
console.log('ðŸ“Š Scheduling Summary:');
console.log(`Total posts scheduled: ${scheduledPosts.length}`);
console.log(`Date range: ${Math.min(...Array.from(usedDatesGlobal))} to ${Math.max(...Array.from(usedDatesGlobal))}`);
console.log('Themes distribution:', Object.keys(usedDatesPerTheme).map(theme => `${theme}: ${usedDatesPerTheme[theme].size} days`));

console.log('âœ… Returning scheduled posts data');
console.log('Sample scheduled post:', scheduledPosts[0] ? Object.keys(scheduledPosts[0]) : 'none');

return {
  scheduled_posts: scheduledPosts,
  total_posts: scheduledPosts.length,
  chat_id: inputData.chat_id,
  user_id: inputData.user_id,
  original_prompt: inputData.original_prompt,
  scheduling_summary: {
    themes_used: Object.keys(usedDatesPerTheme),
    dates_used: Array.from(usedDatesGlobal).sort(),
    date_range_days: 14,
    theme_distribution: Object.fromEntries(
      Object.entries(usedDatesPerTheme).map(([theme, dates]) => [theme, dates.size])
    )
  }
};
