// üîÑ Buffer Post Splitter - Creates individual posts for Buffer API
const data = $input.first().json;
const posts = data.formattedPosts || [];

// Validation
if (!posts || posts.length === 0) {
  throw new Error('Aucun post format√© √† programmer');
}

// üé≤ Smart Scheduling Logic
function generateRandomSchedule(index, total) {
  const now = new Date();

  // For numbered posts: spread over 1-14 days
  // For long posts: schedule sooner (within 1-3 days)
  const isLongPost = index === 0; // Long posts are usually first
  const maxDays = isLongPost ? 3 : 14;
  const minDays = isLongPost ? 0.1 : 1; // Long post can be posted within hours

  const randomDays = Math.random() * (maxDays - minDays) + minDays;
  const randomHours = Math.floor(Math.random() * 16) + 6; // 6 AM to 10 PM
  const randomMinutes = Math.floor(Math.random() * 60);

  const scheduledTime = new Date(now);
  scheduledTime.setDate(now.getDate() + Math.floor(randomDays));
  scheduledTime.setHours(randomHours, randomMinutes, 0, 0);

  return scheduledTime;
}

// üè∑Ô∏è Buffer Profile Mapping
const BUFFER_PROFILES = {
  'Facebook': 'YOUR_FACEBOOK_PROFILE_ID',
  'Instagram': 'YOUR_INSTAGRAM_PROFILE_ID', 
  'LinkedIn': 'YOUR_LINKEDIN_PROFILE_ID',
  'Twitter': 'YOUR_TWITTER_PROFILE_ID'
};

// Create Buffer-ready posts
const bufferPosts = posts.map((post, index) => {
  const profileId = BUFFER_PROFILES[post.platform];

  if (!profileId || profileId.startsWith('YOUR_')) {
    return null; // Skip unconfigured platforms
  }

  const scheduledTime = generateRandomSchedule(index, posts.length);

  // Format content with hashtags
  let finalContent = post.content;
  if (post.hashtags && post.hashtags.length > 0) {
    finalContent += '\n\n' + post.hashtags.join(' ');
  }

  return {
    ...data, // Include all original data
    currentPost: {
      text: finalContent,
      profile_ids: [profileId],
      scheduled_at: Math.floor(scheduledTime.getTime() / 1000),
      platform: post.platform,
      postNumber: post.postNumber,
      postType: post.postType,
      contentId: post.contentId,
      scheduledDateTime: scheduledTime.toISOString(),
      originalPost: post.originalPost || '',
      originalComment: post.originalComment || '',
      characterCount: post.characterCount || finalContent.length,
      facebookReady: post.facebookReady !== false,
      contentStyle: post.contentStyle || 'wisdom',
      isLongPost: post.postType === 'long_formal_post'
    }
  };
}).filter(post => post !== null);

if (bufferPosts.length === 0) {
  throw new Error('Aucun profil Buffer configur√© - v√©rifiez vos IDs de profil');
}

return bufferPosts;

// üé® Advanced Content Formatter - Creates Platform-Specific Posts
const config = $input.first().json;
const agentResponse = config.agentResponse || '';

// Validation
if (!config || !agentResponse) {
  throw new Error('Configuration ou r√©ponse agent manquante');
}

// üì± Platform Specifications
const PLATFORM_SPECS = {
  'Facebook': { 
    maxLength: 2000, 
    tone: 'Conversational', 
    hashtagLimit: 10,
    supportsFormatting: true,
    audience: 'Broad community'
  },
  'Instagram': { 
    maxLength: 2200, 
    tone: 'Visual-focused', 
    hashtagLimit: 30,
    supportsFormatting: true,
    audience: 'Visual engagement'
  },
  'LinkedIn': { 
    maxLength: 1300, 
    tone: 'Professional', 
    hashtagLimit: 5,
    supportsFormatting: true,
    audience: 'Professional network'
  },
  'Twitter': { 
    maxLength: 280, 
    tone: 'Concise', 
    hashtagLimit: 2,
    supportsFormatting: false,
    audience: 'Quick engagement'
  }
};

// üîÑ Content Processing Functions
function extractNumberedPosts(content) {
  const posts = [];
  // Updated regex to handle longer comments (3-10 phrases)
  const postRegex = /\*\*(\d+)\.\s*([^*]+?)\*\*\s*\*\*Commentaire\s*:\*\*\s*([\s\S]*?)(?=\*\*\d+\.|$)/g;
  let match;

  while ((match = postRegex.exec(content)) !== null) {
    const postText = match[2].trim();
    const comment = match[3].trim();

    // Ensure post meets Facebook background limit (80 chars)
    let finalPost = postText;
    if (postText.length > 80) {
      // Try to shorten while keeping meaning
      const shortened = shortenForFacebook(postText);
      finalPost = shortened;
    }

    posts.push({
      number: parseInt(match[1]),
      post: finalPost,
      originalPost: postText,
      comment: comment,
      fullPost: match[0],
      characterCount: finalPost.length,
      facebookReady: finalPost.length <= 80
    });
  }

  return posts;
}

function shortenForFacebook(text) {
  if (text.length <= 80) return text;

  // Smart shortening strategies
  let shortened = text;

  // Remove redundant words
  shortened = shortened.replace(/\s+tr√®s\s+/, ' ');
  shortened = shortened.replace(/\s+vraiment\s+/, ' ');
  shortened = shortened.replace(/\s+exactement\s+/, ' ');

  // Replace long phrases with shorter ones
  shortened = shortened.replace(/quelque chose/, 'qqch');
  shortened = shortened.replace(/quelqu'un/, 'qqn');
  shortened = shortened.replace(/c'est-√†-dire/, 'c√†d');

  // If still too long, truncate intelligently
  if (shortened.length > 80) {
    // Find last complete word before 77 chars (leave room for "...")
    const truncated = shortened.substring(0, 77);
    const lastSpace = truncated.lastIndexOf(' ');
    shortened = truncated.substring(0, lastSpace) + '...';
  }

  return shortened;
}

function createPlatformVersion(post, platform, specs, contentStyle) {
  let content = '';

  if (config.hasNumberedPosts) {
    // Format for numbered posts - use complete post, not title
    content = `${post.number}. ${post.post}\n\n${post.comment}`;
  } else {
    // Format for single content
    content = agentResponse;
  }

  // Platform-specific adjustments based on content style
  if (platform === 'LinkedIn') {
    content = content.replace(/\*\*/g, ''); // Remove bold for LinkedIn
    if (contentStyle === 'sarcasm') {
      content = `ü§î ${content}`; // Thoughtful emoji for sarcasm
    } else if (contentStyle === 'motivation') {
      content = `üí° ${content}`; // Professional motivation
    } else {
      content = `üí≠ ${content}`; // Reflection emoji
    }
  } else if (platform === 'Twitter') {
    // Shorten for Twitter
    if (content.length > specs.maxLength - 20) {
      content = content.substring(0, specs.maxLength - 23) + '...';
    }
  } else if (platform === 'Instagram') {
    if (contentStyle === 'humor') {
      content = `üòÇ ${content} üòÇ`; // Humor emojis
    } else if (contentStyle === 'sarcasm') {
      content = `üôÑ ${content} üôÑ`; // Sarcasm emojis
    } else if (contentStyle === 'roasting') {
      content = `üî• ${content} üî•`; // Fire emojis for roasting
    } else {
      content = `‚ú® ${content} ‚ú®`; // Default visual elements
    }
  } else if (platform === 'Facebook') {
    // Facebook handles all styles well without modification
    // Just ensure proper formatting
  }

  // Ensure character limits
  if (content.length > specs.maxLength) {
    content = content.substring(0, specs.maxLength - 3) + '...';
  }

  return content;
}

function generateHashtags(content, platform, limit, contentStyle) {
  const baseHashtags = [];

  // Style-specific hashtags
  if (contentStyle === 'sarcasm') {
    baseHashtags.push('#sarcasme', '#ironie', '#v√©rit√©', '#realit√©');
  } else if (contentStyle === 'humor') {
    baseHashtags.push('#humour', '#blague', '#rire', '#dr√¥le');
  } else if (contentStyle === 'roasting') {
    baseHashtags.push('#v√©rit√©', '#r√©veil', '#r√©alit√©', '#faits');
  } else if (contentStyle === 'motivation') {
    baseHashtags.push('#motivation', '#inspiration', '#changement', '#succ√®s');
  }

  // Content-based hashtags
  if (content.toLowerCase().includes('amour')) {
    baseHashtags.push('#amour', '#relations', '#couple');
  }
  if (content.toLowerCase().includes('choix') || content.toLowerCase().includes('d√©cision')) {
    baseHashtags.push('#choix', '#d√©cisions', '#vie');
  }

  // Add platform-specific hashtags
  if (platform === 'LinkedIn') {
    if (contentStyle === 'sarcasm') {
      baseHashtags.push('#leadership', '#mindset');
    } else {
      baseHashtags.push('#d√©veloppementpersonnel', '#r√©flexion');
    }
  } else if (platform === 'Instagram') {
    baseHashtags.push('#lifestyle', '#pens√©es', '#citation', '#r√©flexion');
    if (contentStyle === 'humor') {
      baseHashtags.push('#memes', '#funny', '#mood');
    }
  } else if (platform === 'Facebook') {
    baseHashtags.push('#partage', '#communaut√©');
  }

  // Default hashtags
  baseHashtags.push('#philosophie', '#sagesse', '#croissance');

  return [...new Set(baseHashtags)].slice(0, limit);
}

// üéØ Main Processing
let formattedPosts = [];

try {
  if (config.hasNumberedPosts) {
    // Process multiple numbered posts
    const extractedPosts = extractNumberedPosts(agentResponse);

    if (extractedPosts.length === 0) {
      throw new Error('Aucun post num√©rot√© trouv√© dans la r√©ponse de l\'agent');
    }

    extractedPosts.forEach(post => {
      config.targetPlatforms.forEach(platform => {
        const specs = PLATFORM_SPECS[platform] || PLATFORM_SPECS['Facebook'];
        const platformContent = createPlatformVersion(post, platform, specs, config.contentStyle);
        const hashtags = generateHashtags(platformContent, platform, specs.hashtagLimit, config.contentStyle);

        formattedPosts.push({
          contentId: `${config.contentId}_post${post.number}_${platform.toLowerCase()}`,
          platform,
          postNumber: post.number,
          content: platformContent,
          hashtags,
          originalPost: post.post,
          originalComment: post.comment,
          characterCount: post.characterCount,
          facebookReady: post.facebookReady,
          contentStyle: config.contentStyle,
          readyForScheduling: true,
          postType: 'numbered_short_post'
        });
      });
    });

    // Create one long post for context (formal language)
    const longPostContent = `R√©flexion du jour :\n\n${config.originalInput}\n\nCette question m√©rite qu'on s'y arr√™te. Dans nos relations, nous cherchons souvent des preuves d'amour qui correspondent √† notre propre fa√ßon d'aimer, sans r√©aliser que l'autre exprime peut-√™tre ses sentiments diff√©remment. La compr√©hension mutuelle passe par l'acceptation de ces diff√©rences.`;

    config.targetPlatforms.forEach(platform => {
      const specs = PLATFORM_SPECS[platform] || PLATFORM_SPECS['Facebook'];
      let content = longPostContent;

      if (content.length > specs.maxLength) {
        content = content.substring(0, specs.maxLength - 3) + '...';
      }

      formattedPosts.push({
        contentId: `${config.contentId}_long_${platform.toLowerCase()}`,
        platform,
        postNumber: 0, // 0 indicates long post
        content,
        hashtags: generateHashtags(content, platform, specs.hashtagLimit, config.contentStyle),
        contentStyle: config.contentStyle,
        readyForScheduling: true,
        postType: 'long_formal_post'
      });
    });

  } else {
    // Process single content
    config.targetPlatforms.forEach(platform => {
      const specs = PLATFORM_SPECS[platform] || PLATFORM_SPECS['Facebook'];
      const content = createPlatformVersion({ post: agentResponse, comment: '' }, platform, specs, config.contentStyle);
      const hashtags = generateHashtags(content, platform, specs.hashtagLimit, config.contentStyle);

      formattedPosts.push({
        contentId: `${config.contentId}_single_${platform.toLowerCase()}`,
        platform,
        postNumber: 1,
        content,
        hashtags,
        contentStyle: config.contentStyle,
        readyForScheduling: true,
        postType: 'single_post'
      });
    });
  }
} catch (error) {
  throw new Error(`Erreur lors du formatage: ${error.message}`);
}

// üìä Generate Summary
const summary = {
  totalPosts: formattedPosts.length,
  numberedPosts: formattedPosts.filter(p => p.postType === 'numbered_short_post').length,
  longPosts: formattedPosts.filter(p => p.postType === 'long_formal_post').length,
  platformsTargeted: config.targetPlatforms,
  processingMode: config.processingMode,
  qualityScore: config.qualityScore
};

return {
  ...config,
  formattedPosts,
  summary,
  processingComplete: true,
  readyForBuffer: true
};

// ü§ñ Smart Content Analyzer & Command Detector
const userInput = $input.first().json.userInput || '';
const agentResponse = $input.first().json.agentResponse || '';
const messageData = $input.first().json.messageData || {};

// Validate inputs
if (!userInput || userInput.length < 3) {
  throw new Error('Input trop court - minimum 3 caract√®res requis');
}

if (!agentResponse || agentResponse.length < 10) {
  throw new Error('R√©ponse de l\'agent invalide ou trop courte');
}

// üîç Advanced Command Detection
const COMMANDS = {
  'BC': { action: 'background_content', priority: 1, postType: 'Background' },
  'BCW': { action: 'background_with_comments', priority: 2, postType: 'Background' },
  'SC': { action: 'summary_content', priority: 3, postType: 'Short' },
  'BG': { action: 'background_generation', priority: 2, postType: 'Full' },
  'CGO': { action: 'grammar_only', priority: 1, postType: 'Original' },
  'MIB': { action: 'make_better', priority: 2, postType: 'Improved' },
  'PAI': { action: 'post_as_is', priority: 1, postType: 'Original' },
  'TL': { action: 'translate', priority: 1, postType: 'Translated' },
  'KPC': { action: 'key_points', priority: 3, postType: 'Points' }
};

function detectCommand(text) {
  const upperText = text.toUpperCase();
  let command = null;
  let parameters = {};

  // Check for explicit commands
  for (const [cmd, config] of Object.entries(COMMANDS)) {
    if (upperText.startsWith(cmd + ' ') || upperText === cmd) {
      command = cmd;
      break;
    }
  }

  // Extract parameters
  const platformMatch = text.match(/Platform[:\s]+(.*?)(?=\s+[A-Z]+:|$)/i);
  if (platformMatch) {
    parameters.platforms = platformMatch[1].split(',').map(p => p.trim());
  }

  const languageMatch = text.match(/Language[:\s]+(.*?)(?=\s+[A-Z]+:|$)/i);
  if (languageMatch) {
    parameters.languages = languageMatch[1].split(',').map(l => l.trim());
  }

  const toneMatch = text.match(/Tone[:\s]+(\w+)/i);
  if (toneMatch) {
    parameters.tone = toneMatch[1];
  }

  // Clean content
  let cleanContent = text;
  if (command) {
    cleanContent = text.replace(new RegExp(`^${command}\\s*`, 'i'), '');
    cleanContent = cleanContent
      .replace(/Platform[:\s]+[^\n]+/gi, '')
      .replace(/Language[:\s]+[^\n]+/gi, '')
      .replace(/Tone[:\s]+\w+/gi, '')
      .trim();
  }

  return { command, parameters, cleanContent, hasCommand: !!command };
}

// üìä Content Analysis
function analyzeContent(content, agentResponse) {
  const commandAnalysis = detectCommand(content);

  // Check if agent created multiple numbered posts
  const hasNumberedPosts = agentResponse.match(/\*\*\d+\./g)?.length > 1;
  const postCount = agentResponse.match(/\*\*\d+\./g)?.length || 0;

  // Detect content type
  let contentType = 'text';
  if (content.match(/https?:\/\/.+\.(jpg|jpeg|png|gif)/i)) contentType = 'image_url';
  if (content.match(/https?:\/\/.+\.(mp4|avi|mov)/i)) contentType = 'video_url';
  if (messageData.document) contentType = 'document';
  if (messageData.photo) contentType = 'image';
  if (messageData.video) contentType = 'video';

  // Detect content style/tone
  let contentStyle = 'wisdom';
  const lowerContent = content.toLowerCase();
  const lowerResponse = agentResponse.toLowerCase();

  if (lowerContent.includes('lol') || lowerContent.includes('mdr') || 
      lowerResponse.includes('blague') || lowerResponse.includes('rigol')) {
    contentStyle = 'humor';
  } else if (lowerContent.includes('ironique') || lowerResponse.includes('sarcas') ||
            lowerResponse.includes('ah, le fameux') || lowerResponse.includes('g√©n√©ralement donn√© par')) {
    contentStyle = 'sarcasm';
  } else if (lowerContent.includes('critique') || lowerContent.includes('roast') ||
            lowerResponse.includes('v√©rit√© qui d√©range') || lowerResponse.includes('r√©veil brutal')) {
    contentStyle = 'roasting';
  } else if (lowerContent.includes('motiv') || lowerResponse.includes('inspiration') ||
            lowerResponse.includes('tu peux') || lowerResponse.includes('r√©ussi')) {
    contentStyle = 'motivation';
  }

  // Quality scoring with style bonus
  let qualityScore = 50;
  if (hasNumberedPosts) qualityScore += 30;
  if (postCount >= 5) qualityScore += 20;
  if (agentResponse.includes('**Commentaire :**')) qualityScore += 20;
  if (agentResponse.length > 500) qualityScore += 10;

  // Style-specific bonuses
  if (contentStyle === 'sarcasm' && agentResponse.includes('Ah,')) qualityScore += 15;
  if (contentStyle === 'humor' && agentResponse.match(/üòÇ|üòÖ|ü§£/)) qualityScore += 10;
  if (contentStyle === 'roasting' && agentResponse.includes('v√©rit√©')) qualityScore += 15;

  return {
    ...commandAnalysis,
    contentType,
    contentStyle,
    hasNumberedPosts,
    postCount,
    qualityScore: Math.min(100, qualityScore),
    isAutoFormat: !commandAnalysis.hasCommand && hasNumberedPosts
  };
}

// üéØ Platform Configuration
const defaultPlatforms = ['Facebook', 'Instagram', 'LinkedIn'];
const defaultLanguages = ['French'];
const defaultTone = 'Authentic';

// Main Analysis
const analysis = analyzeContent(userInput, agentResponse);

// Generate unique content ID
const contentId = `content_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

// üöÄ Create processing configuration
const processingConfig = {
  // Core identifiers
  contentId,
  messageId: $input.first().json.messageId,
  chatId: $input.first().json.chatId,
  userId: $input.first().json.userId,
  timestamp: $input.first().json.timestamp,

  // Content data
  originalInput: userInput,
  agentResponse: agentResponse,
  cleanContent: analysis.cleanContent,

  // Analysis results
  hasCommand: analysis.hasCommand,
  command: analysis.command,
  parameters: analysis.parameters,
  contentType: analysis.contentType,

  // Content intelligence
  isAutoFormat: analysis.isAutoFormat,
  hasNumberedPosts: analysis.hasNumberedPosts,
  postCount: analysis.postCount,
  qualityScore: analysis.qualityScore,
  contentStyle: analysis.contentStyle,

  // Processing configuration
  targetPlatforms: analysis.parameters.platforms || defaultPlatforms,
  languages: analysis.parameters.languages || defaultLanguages,
  tone: analysis.parameters.tone || defaultTone,

  // Post type determination
  postType: analysis.command ? COMMANDS[analysis.command]?.postType : 
           (analysis.hasNumberedPosts ? 'Multiple Short Posts' : 'Full Post'),

  // Processing flags
  needsPlatformOptimization: true,
  needsBufferScheduling: true,
  readyForPosting: analysis.qualityScore > 70,

  // Agent intelligence metadata
  processingMode: analysis.hasCommand ? 'COMMAND_MODE' : 'AUTO_MODE',
  contentStrategy: analysis.hasNumberedPosts ? 'MULTIPLE_POSTS' : 'SINGLE_POST',

  // File information
  hasFiles: !!(messageData.document || messageData.photo || messageData.video),
  fileInfo: messageData.document || messageData.photo || messageData.video || null
};

return processingConfig;
