// üß† Enhanced Agentic Parser - Short Posts + Comments Focus
const telegramData = $input.first().json;
const message = telegramData.message || {};
const messageText = message.text || '';
const userId = message.from?.id;
const chatId = message.chat?.id;
const messageId = message.message_id;
const files = message.document || message.photo || message.video || message.audio;
const timestamp = new Date().toISOString();

// Enhanced Agent Configuration for Short Posts
const AGENT_CONFIG = {
  autonomyLevel: 'high',
  autoApprovalThreshold: 80,
  focusFormat: 'short_post_with_comment', // Main focus
  defaultPlatforms: ['Facebook', 'Twitter'],
  randomScheduling: true,
  trendingTopicFilling: true,
  humanReadableOutput: true,
  defaultLanguage: 'french'
};

// Short Post Platform Configuration
const PLATFORM_CONFIG = {
  'Facebook': {
    shortPostLength: 150,
    commentLength: 500,
    tone: 'engaging',
    hashtagLimit: 5,
    optimalTimes: ['08:00', '12:00', '18:00', '20:00']
  },
  'Twitter': {
    shortPostLength: 200,
    commentLength: 400,
    tone: 'concise',
    hashtagLimit: 3,
    optimalTimes: ['09:00', '13:00', '17:00', '21:00']
  },
  'LinkedIn': {
    shortPostLength: 180,
    commentLength: 600,
    tone: 'professional',
    hashtagLimit: 4,
    optimalTimes: ['08:00', '12:00', '17:00']
  },
  'Instagram': {
    shortPostLength: 160,
    commentLength: 450,
    tone: 'inspirational',
    hashtagLimit: 10,
    optimalTimes: ['11:00', '14:00', '19:00']
  }
};

// Content Categories for Short Posts
const CONTENT_CATEGORIES = {
  'life_wisdom': {
    keywords: ['vie', 'wisdom', 'choix', 'decision', 'sagesse', 'experience'],
    tone: 'reflective',
    engagement_style: 'thought_provoking'
  },
  'motivation': {
    keywords: ['motivation', 'success', 'effort', 'objectif', 'r√™ve', 'r√©ussir'],
    tone: 'inspiring',
    engagement_style: 'empowering'
  },
  'business_insight': {
    keywords: ['business', 'entrepreneur', 'strategy', 'leadership', 'innovation'],
    tone: 'authoritative',
    engagement_style: 'educational'
  },
  'personal_development': {
    keywords: ['d√©veloppement', 'growth', 'mindset', 'habitude', 'discipline'],
    tone: 'encouraging',
    engagement_style: 'actionable'
  },
  'relationships': {
    keywords: ['relation', 'amiti√©', 'famille', 'confiance', 'communication'],
    tone: 'warm',
    engagement_style: 'relatable'
  }
};

// Special Commands for Short Post Format
const SHORT_POST_COMMANDS = {
  'SP': { action: 'short_posts_generation', count: 5, focus: 'varied' },
  'QSP': { action: 'quote_short_posts', count: 6, focus: 'wisdom' },
  'MSP': { action: 'motivational_short_posts', count: 4, focus: 'inspiration' },
  'BSP': { action: 'business_short_posts', count: 5, focus: 'insights' },
  'TSP': { action: 'trending_short_posts', count: 3, focus: 'current' },
  'FILL': { action: 'fill_empty_slots', count: 'auto', focus: 'trending' }
};

// Advanced Command Detection
function detectCommandAndIntent(text) {
  const upperText = text.toUpperCase();

  // Check for explicit short post commands
  for (const [cmd, config] of Object.entries(SHORT_POST_COMMANDS)) {
    if (upperText.startsWith(cmd)) {
      const countMatch = text.match(/count:\s*(\d+)|\b(\d+)\s*(post|message)/i);
      const count = countMatch ? parseInt(countMatch[1] || countMatch[2]) : config.count;

      return {
        command: cmd,
        action: config.action,
        contentCount: count === 'auto' ? 'auto' : Math.min(Math.max(count, 3), 8),
        focus: config.focus,
        isExplicitCommand: true
      };
    }
  }

  // Natural language detection for short posts
  const shortPostIndicators = [
    'court', 'short', 'quick', 'rapide', 'bref',
    'commentaire', 'comment', 'explication',
    'variation', 'versions', 'diff√©rent'
  ];

  const hasShortPostIntent = shortPostIndicators.some(indicator => 
    text.toLowerCase().includes(indicator)
  );

  // Content type detection
  let contentFocus = 'varied';
  if (text.toLowerCase().includes('motivation') || text.toLowerCase().includes('inspire')) {
    contentFocus = 'motivation';
  } else if (text.toLowerCase().includes('business') || text.toLowerCase().includes('entrepreneur')) {
    contentFocus = 'business';
  } else if (text.toLowerCase().includes('quote') || text.toLowerCase().includes('citation')) {
    contentFocus = 'wisdom';
  }

  return {
    command: hasShortPostIntent ? 'SP' : 'AUTO',
    action: 'short_posts_generation',
    contentCount: hasShortPostIntent ? 5 : 4,
    focus: contentFocus,
    isExplicitCommand: false
  };
}

// Content Analysis for Short Post Optimization
function analyzeContentForShortPosts(content) {
  const lowerContent = content.toLowerCase();

  // Category detection
  let detectedCategory = 'life_wisdom'; // default
  let maxScore = 0;

  for (const [category, config] of Object.entries(CONTENT_CATEGORIES)) {
    const score = config.keywords.filter(keyword => 
      lowerContent.includes(keyword)
    ).length;

    if (score > maxScore) {
      maxScore = score;
      detectedCategory = category;
    }
  }

  // Quality indicators for short posts
  const qualityFactors = {
    hasMetaphor: /comme|tel que|√† la mani√®re|ressemble/.test(lowerContent),
    hasQuestion: /\?/.test(content),
    hasNumbers: /\d+/.test(content),
    hasEmotionalWords: /(vie|mort|amour|peur|r√™ve|√©chec|succ√®s)/.test(lowerContent),
    hasActionWords: /(choix|d√©cision|action|effort|lutte)/.test(lowerContent),
    properLength: content.length > 30 && content.length < 500,
    hasContrast: /(mais|cependant|pourtant|tandis que)/.test(lowerContent)
  };

  const qualityScore = Object.values(qualityFactors).filter(Boolean).length * 12;

  return {
    category: detectedCategory,
    categoryConfig: CONTENT_CATEGORIES[detectedCategory],
    qualityScore: Math.min(100, qualityScore + 40), // Base 40 + factors
    qualityFactors,
    wordCount: content.split(' ').length,
    hasQuoteStructure: content.includes('‚Ä¶') || content.includes('...'),
    emotionalIntensity: (lowerContent.match(/(jamais|toujours|tout|rien|√©norme|terrible|magnifique)/g) || []).length
  };
}

// Smart Random Scheduling for Variety
function generateRandomSchedule(count, avoidSameDay = true) {
  const now = new Date();
  const schedules = [];
  const usedDays = new Set();

  // Platform optimal times
  const allOptimalTimes = [
    ...PLATFORM_CONFIG.Facebook.optimalTimes,
    ...PLATFORM_CONFIG.Twitter.optimalTimes
  ].filter((time, index, arr) => arr.indexOf(time) === index);

  for (let i = 0; i < count; i++) {
    let attempts = 0;
    let scheduleDate;

    do {
      // Random day within next 2-4 weeks
      const daysOffset = Math.floor(Math.random() * 28) + 1;
      scheduleDate = new Date(now);
      scheduleDate.setDate(now.getDate() + daysOffset);

      // Skip weekends 30% of the time for variety
      const isWeekend = scheduleDate.getDay() === 0 || scheduleDate.getDay() === 6;
      if (isWeekend && Math.random() < 0.3) {
        attempts++;
        continue;
      }

      const dayKey = scheduleDate.toDateString();

      if (!avoidSameDay || !usedDays.has(dayKey) || attempts > 15) {
        // Random optimal time
        const randomTime = allOptimalTimes[Math.floor(Math.random() * allOptimalTimes.length)];
        const [hours, minutes] = randomTime.split(':').map(Number);

        scheduleDate.setHours(hours, minutes + Math.floor(Math.random() * 30), 0, 0);

        if (avoidSameDay) {
          usedDays.add(dayKey);
        }
        break;
      }
      attempts++;
    } while (attempts <= 20);

    schedules.push(scheduleDate.toISOString());
  }

  return schedules.sort();
}

// Generate unique ID
const generateAgentId = () => {
  return `agent_short_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
};

// Main Processing
const agentId = generateAgentId();
const commandAnalysis = detectCommandAndIntent(messageText);
const contentAnalysis = analyzeContentForShortPosts(messageText);

// Generate schedule with randomization
const schedules = generateRandomSchedule(
  commandAnalysis.contentCount === 'auto' ? 5 : commandAnalysis.contentCount,
  true // Avoid same day posting
);

// Determine if this is a trending topic fill request
const isTrendingFill = commandAnalysis.command === 'FILL' || 
                      messageText.toLowerCase().includes('fill') || 
                      messageText.toLowerCase().includes('trending');

// Auto-approval logic for short posts
const shouldAutoApprove = contentAnalysis.qualityScore >= AGENT_CONFIG.autoApprovalThreshold &&
                         commandAnalysis.focus !== 'current' && // Be careful with trending topics
                         !isTrendingFill;

return {
  // Core identifiers
  agentId,
  messageId: agentId,
  telegramMessageId: messageId,
  userId,
  chatId,
  timestamp,

  // Content analysis
  originalContent: messageText,
  contentAnalysis,
  commandAnalysis,

  // Processing configuration
  processingStrategy: 'short_posts_with_comments',
  targetPlatforms: ['Facebook', 'Twitter'], // Focus platforms
  contentCount: commandAnalysis.contentCount,
  contentFocus: commandAnalysis.focus,

  // Scheduling
  schedules,
  randomScheduling: true,

  // Agent decisions
  shouldAutoApprove,
  isTrendingFill,
  requiresCommentary: true, // Always true for this format

  // Format requirements
  outputFormat: {
    type: 'short_post_with_comment',
    shortPostMaxLength: 180,
    commentMaxLength: 500,
    includeNumbers: true, // For numbered posts like 1., 2., etc.
    language: 'french',
    tone: contentAnalysis.categoryConfig?.tone || 'engaging'
  },

  // File info
  hasMedia: !!files,
  fileInfo: files ? {
    type: message.document ? 'document' : message.photo ? 'image' : message.video ? 'video' : 'audio',
    fileId: files.file_id || files[files.length - 1]?.file_id
  } : null,

  // Agent config
  agentConfig: AGENT_CONFIG,

  // Next action routing
  nextAction: {
    node: isTrendingFill ? 'trending_topic_generation' : 'short_post_generation',
    priority: isTrendingFill ? 'high' : 'medium',
    autoProcess: shouldAutoApprove
  }
};

