// üîÑ Process Short Posts with Comments
const agentData = $('Short Post Intelligence Engine').first().json;
const aiResponse = $input.first().json;

// Parse AI response
let generatedPosts = [];
try {
  const aiContent = aiResponse.choices[0].message.content;
  generatedPosts = JSON.parse(aiContent);
} catch (error) {
  console.log('Parsing fallback for short posts');
  // Fallback parsing
  generatedPosts = [{
    numero: 1,
    post_court: agentData.originalContent.substring(0, 180),
    commentaire: 'Commentaire g√©n√©r√© automatiquement bas√© sur le contenu source.'
  }];
}

// Ensure we have the right number of posts
while (generatedPosts.length < agentData.contentCount && generatedPosts.length > 0) {
  const basePage = generatedPosts[generatedPosts.length % generatedPosts.length];
  generatedPosts.push({
    numero: generatedPosts.length + 1,
    post_court: basePage.post_court + ' (variation)',
    commentaire: basePage.commentaire + ' Perspective alternative.'
  });
}

// Quality scoring for short posts
function calculateShortPostQuality(post, comment, agentContext) {
  let score = 50;

  // Post length optimization (short but meaningful)
  if (post.length > 30 && post.length <= 180) score += 15;
  else if (post.length > 15) score += 8;

  // Comment depth and value
  if (comment.length > 150) score += 10;
  if (comment.length > 300) score += 5;

  // Engagement elements
  if (post.includes('?')) score += 8;
  if (comment.includes('exemple') || comment.includes('par exemple')) score += 5;
  if (post.match(/[.!‚Ä¶]/)) score += 5;

  // French language quality indicators
  if (comment.includes(':') && comment.split(':').length > 1) score += 5; // Good structure
  if (post.match(/\b(tu|vous|ton|votre)\b/)) score += 8; // Direct address

  // Content quality
  if (comment.includes('recherche') || comment.includes('√©tude')) score += 5;
  if (post.length < comment.length) score += 10; // Good ratio

  return Math.min(100, Math.max(30, score));
}

// Process each generated post
const processedPosts = generatedPosts.slice(0, agentData.contentCount).map((post, index) => {
  const contentId = `${agentData.agentId}_short_${post.numero || (index + 1)}`;
  const scheduleDate = agentData.schedules[index] || new Date(Date.now() + (index + 1) * 24 * 60 * 60 * 1000).toISOString();

  const qualityScore = calculateShortPostQuality(
    post.post_court || '',
    post.commentaire || '',
    agentData
  );

  // Auto-approval based on quality and agent settings
  const shouldAutoPost = qualityScore >= agentData.agentConfig.autoApprovalThreshold &&
                        agentData.shouldAutoApprove &&
                        !agentData.isTrendingFill;

  return {
    // Core identifiers
    contentId,
    agentId: agentData.agentId,
    parentMessageId: agentData.messageId,
    postNumber: post.numero || (index + 1),
    totalPosts: agentData.contentCount,

    // Content format
    format: 'short_post_with_comment',
    postCourt: (post.post_court || '').substring(0, 180),
    commentaire: (post.commentaire || '').substring(0, 500),
    fullContent: `${post.post_court || ''}\n\nCommentaire :\n${post.commentaire || ''}`,

    // Trending topic info (if applicable)
    sujetTendance: post.sujet_tendance || null,

    // Platform targeting
    targetPlatforms: agentData.targetPlatforms,
    primaryPlatform: 'Facebook',

    // Scheduling with randomization
    scheduledDate: scheduleDate,
    isRandomScheduled: true,
    postingStrategy: 'random_distributed',

    // Quality and approval
    qualityScore,
    shouldAutoPost,
    posted: false,
    postingStatus: shouldAutoPost ? 'Auto-Approved' : 'Pending Review',

    // Content metadata
    sourceCategory: agentData.contentAnalysis.category,
    contentFocus: agentData.contentFocus,
    detectedLanguage: 'french',

    // Character counts
    postLength: (post.post_court || '').length,
    commentLength: (post.commentaire || '').length,

    // Agent context
    originalSource: agentData.originalContent,
    isTrendingContent: agentData.isTrendingFill,

    // Timestamps
    createdAt: new Date().toISOString(),

    // Platform-specific versions
    platformVersions: {
      Facebook: {
        post: (post.post_court || '').substring(0, 150),
        comment: (post.commentaire || '').substring(0, 500)
      },
      Twitter: {
        post: (post.post_court || '').substring(0, 200),
        comment: (post.commentaire || '').substring(0, 400)
      }
    }
  };
});

// Performance metrics
const performanceMetrics = {
  totalGenerated: processedPosts.length,
  averageQualityScore: Math.round(processedPosts.reduce((sum, p) => sum + p.qualityScore, 0) / processedPosts.length),
  autoApprovedCount: processedPosts.filter(p => p.shouldAutoPost).length,
  averagePostLength: Math.round(processedPosts.reduce((sum, p) => sum + p.postLength, 0) / processedPosts.length),
  averageCommentLength: Math.round(processedPosts.reduce((sum, p) => sum + p.commentLength, 0) / processedPosts.length),
  scheduleSpanDays: Math.ceil((new Date(agentData.schedules[agentData.schedules.length - 1]) - new Date(agentData.schedules[0])) / (1000 * 60 * 60 * 24)),
  randomSchedulingUsed: true,
  contentFormat: 'short_post_with_comment'
};

return {
  agentData,
  processedPosts,
  performanceMetrics,
  processingSuccess: true,
  formatUsed: 'short_post_with_comment',
  nextAction: processedPosts.filter(p => p.shouldAutoPost).length > 0 ? 'auto_posting' : 'human_review'
};

// Split posts for individual processing
const processingData = $input.first().json;
return processingData.processedPosts.map(post => ({
  ...post,
  batchMetrics: processingData.performanceMetrics,
  agentContext: processingData.agentData
}));

// üöÄ Random Auto-Posting Logic
const postData = $input.first().json;
const now = new Date();
const scheduledTime = new Date(postData.scheduledDate);

// Check if it's time to post (within 1 hour of scheduled time or immediate)
const timeDiff = Math.abs(scheduledTime - now) / (1000 * 60); // minutes
const shouldPostNow = timeDiff <= 60 || postData.postingStrategy === 'immediate';

if (!shouldPostNow) {
  return {
    ...postData,
    postingDecision: 'scheduled_for_later',
    nextCheckTime: scheduledTime.toISOString(),
    message: `Post programm√© pour ${scheduledTime.toLocaleString('fr-FR')}`
  };
}

// Generate platform-specific content
const platformPosts = postData.targetPlatforms.map(platform => {
  const platformVersion = postData.platformVersions[platform] || {
    post: postData.postCourt,
    comment: postData.commentaire
  };

  // Format for the platform
  let finalContent = `${postData.postNumber}.\n${platformVersion.post}\n\nCommentaire :\n${platformVersion.comment}`;

  // Platform-specific adjustments
  if (platform === 'Twitter') {
    // Split into thread if too long
    if (finalContent.length > 280) {
      const tweet1 = `${postData.postNumber}. ${platformVersion.post}`;
      const tweet2 = `Commentaire : ${platformVersion.comment}`;
      finalContent = { thread: [tweet1, tweet2] };
    }
  }

  return {
    platform,
    content: finalContent,
    postNumber: postData.postNumber,
    readyToPost: true,
    isRandomPost: true
  };
});

return {
  ...postData,
  postingDecision: 'posting_now',
  platformPosts,
  postingTimestamp: now.toISOString(),
  autoPosted: true,
  postingStatus: 'Auto-Posted Successfully',
  randomPostExecuted: true
};

// üîç Analyze schedule for gaps and auto-posting
const sheetData = $input.first().json;
const today = new Date();
const todayStr = today.toISOString().split('T')[0];

// Parse sheet data
const headers = sheetData[0] || [];
const rows = sheetData.slice(1);

// Find scheduled date column
const scheduledDateIndex = headers.findIndex(h => h.includes('Scheduled Date'));
const postedIndex = headers.findIndex(h => h.includes('Posted'));
const statusIndex = headers.findIndex(h => h.includes('Posting Status'));

if (scheduledDateIndex === -1) {
  return { action: 'no_schedule_column', message: 'Schedule column not found' };
}

// Check for today's posts
const todaysPosts = rows.filter(row => {
  const scheduleDate = row[scheduledDateIndex];
  if (!scheduleDate) return false;

  const postDate = new Date(scheduleDate).toISOString().split('T')[0];
  return postDate === todayStr;
});

// Check for posts ready to auto-post
const readyToPosts = todaysPosts.filter(row => {
  const posted = row[postedIndex];
  const status = row[statusIndex];
  return posted !== 'true' && (status === 'Auto-Approved' || status === 'Auto-Posted Successfully');
});

// Check for empty slots in next 7 days
const nextWeek = new Date(today);
nextWeek.setDate(today.getDate() + 7);

const upcomingDays = [];
for (let i = 0; i < 7; i++) {
  const checkDate = new Date(today);
  checkDate.setDate(today.getDate() + i);
  const checkDateStr = checkDate.toISOString().split('T')[0];

  const dayPosts = rows.filter(row => {
    const scheduleDate = row[scheduledDateIndex];
    if (!scheduleDate) return false;
    const postDate = new Date(scheduleDate).toISOString().split('T')[0];
    return postDate === checkDateStr;
  });

  upcomingDays.push({
    date: checkDateStr,
    postsCount: dayPosts.length,
    isEmpty: dayPosts.length === 0
  });
}

const emptyDays = upcomingDays.filter(day => day.isEmpty);

return {
  action: 'schedule_analysis',
  today: todayStr,
  todaysPostsCount: todaysPosts.length,
  readyToPostCount: readyToPosts.length,
  emptyDaysCount: emptyDays.length,
  emptyDays: emptyDays.map(d => d.date),
  upcomingSchedule: upcomingDays,
  needsTrendingFill: emptyDays.length > 2,
  hasAutoPostsToday: readyToPosts.length > 0,

  // Auto-actions needed
  recommendedActions: {
    autoPostToday: readyToPosts.length > 0,
    fillEmptySlots: emptyDays.length > 2,
    generateTrendingContent: emptyDays.length > 3
  }
};
