// Extract the user message from Telegram
const message = $input.item.json.message?.text || '';
const chatId = $input.item.json.message?.chat?.id;
const userId = $input.item.json.message?.from?.id;
const userName = $input.item.json.message?.from?.first_name || 'User';

// Store original message and user info
return {
  user_prompt: message,
  chat_id: chatId,
  user_id: userId,
  user_name: userName,
  timestamp: new Date().toISOString()
};

// Parse AI response and structure the content - GENERATE 20, SELECT BEST 5-10
let aiResponse;

// Debug: Log the raw response structure
console.log('Raw AI Response:', JSON.stringify($input.item.json, null, 2));

// Handle different LangChain response structures
if ($input.item.json.message && $input.item.json.message.content) {
  aiResponse = $input.item.json.message.content;
} else if ($input.item.json.choices && $input.item.json.choices[0]) {
  aiResponse = $input.item.json.choices[0].message.content;
} else if ($input.item.json.text) {
  aiResponse = $input.item.json.text;
} else if ($input.item.json.content) {
  aiResponse = $input.item.json.content;
} else if (typeof $input.item.json === 'string') {
  aiResponse = $input.item.json;
} else {
  aiResponse = JSON.stringify($input.item.json);
}

console.log('Extracted AI Response:', aiResponse);

const originalPrompt = $input.item.json.user_prompt || $('Extract Message').item.json.user_prompt;

// Extract posts using regex - handle different formats
const postPattern = /=== POST (\d+) ===\s*(?:POST:\s*)?(.+?)\s*COMMENTAIRE:\s*(.+?)\s*TAGS:\s*(.+?)(?=\s*===\s*POST|$)/gs;
const allPosts = [];
let match;

while ((match = postPattern.exec(aiResponse)) !== null) {
  const content = match[2].trim();
  const characterCount = content.length;

  // More lenient validation (75-120 characters) to get more posts through
  const isValid = characterCount >= 75 && characterCount <= 120;

  allPosts.push({
    post_number: parseInt(match[1]),
    content: content,
    comment: match[3].trim(),
    tags: match[4].trim().split(/[,\s#]+/).filter(tag => tag.length > 0).map(tag => tag.startsWith('#') ? tag : '#' + tag),
    character_count: characterCount,
    valid: isValid,
    rejection_reason: isValid ? null : characterCount < 75 ? `trop court (manque ${75 - characterCount})` : `trop long (${characterCount - 120} en trop)`
  });
}

// If regex fails, try alternative parsing
if (allPosts.length === 0) {
  console.log('Regex failed, trying alternative parsing...');
  const lines = aiResponse.split('\n').filter(line => line.trim());
  let currentPost = null;

  lines.forEach((line, index) => {
    console.log(`Line ${index}: ${line}`);

    if (line.includes('POST:') || (line.includes('POST') && line.includes('==='))) {
      if (currentPost && currentPost.content) {
        // Validate before adding
        const characterCount = currentPost.content.length;
        currentPost.character_count = characterCount;
        currentPost.valid = characterCount >= 75 && characterCount <= 120;
        currentPost.rejection_reason = currentPost.valid ? null : characterCount < 75 ? `trop court (manque ${75 - characterCount})` : `trop long (${characterCount - 120} en trop)`;
        allPosts.push(currentPost);
      }
      currentPost = {
        post_number: allPosts.length + 1,
        content: line.replace(/POST:/i, '').replace(/===/g, '').trim(),
        comment: '',
        tags: []
      };
    } else if (line.includes('COMMENTAIRE:')) {
      if (currentPost) currentPost.comment = line.replace(/COMMENTAIRE:/i, '').trim();
    } else if (line.includes('TAGS:')) {
      if (currentPost) {
        currentPost.tags = line.replace(/TAGS:/i, '').trim().split(/[,\s#]+/)
          .filter(tag => tag.length > 0)
          .map(tag => tag.startsWith('#') ? tag : '#' + tag);
      }
    } else if (currentPost && !currentPost.content && line.trim().length > 0) {
      currentPost.content = line.trim();
    }
  });

  if (currentPost && currentPost.content) {
    const characterCount = currentPost.content.length;
    currentPost.character_count = characterCount;
    currentPost.valid = characterCount >= 75 && characterCount <= 120;
    currentPost.rejection_reason = currentPost.valid ? null : characterCount < 75 ? `trop court (manque ${75 - characterCount})` : `trop long (${characterCount - 120} en trop)`;
    allPosts.push(currentPost);
  }
}

// Filter and select the best posts
const validPosts = allPosts.filter(post => post.valid);
const invalidPosts = allPosts.filter(post => !post.valid);

// Select the best 5-10 valid posts (prefer those closer to 90-100 characters)
const selectedPosts = validPosts
  .sort((a, b) => {
    // Prefer posts closer to 90-100 characters (sweet spot)
    const scoreA = Math.abs(95 - a.character_count);
    const scoreB = Math.abs(95 - b.character_count);
    return scoreA - scoreB;
  })
  .slice(0, 10); // Take best 10 max

console.log(`Generated: ${allPosts.length}, Valid: ${validPosts.length}, Selected: ${selectedPosts.length}, Invalid: ${invalidPosts.length}`);

if (invalidPosts.length > 0) {
  console.log('Invalid posts details:', JSON.stringify(invalidPosts.map(p => ({content: p.content.substring(0, 50), chars: p.character_count, reason: p.rejection_reason})), null, 2));
}

// Generate preview for Telegram
let preview = `üéØ S√âLECTION AUTOMATIQUE (Validateur: 75-120 caract√®res)\n`;
preview += `üìä G√©n√©r√©: ${allPosts.length} | Valide: ${validPosts.length} | S√©lectionn√©: ${selectedPosts.length} | Rejet√©: ${invalidPosts.length}\n\n`;

if (selectedPosts.length > 0) {
  selectedPosts.forEach((post, index) => {
    preview += `üìù POST ${index + 1} (${post.character_count} car.):\n`;
    preview += `${post.content}\n\n`;
    preview += `üí¨ ${post.comment}\n\n`;
    preview += `üè∑Ô∏è ${post.tags.join(' ')}\n\n‚∏ª\n\n`;
  });
} else if (validPosts.length > 0) {
  // Fallback: show some valid posts even if none selected
  preview += `‚ö†Ô∏è Aucun post parfait, mais voici les meilleurs disponibles:\n\n`;
  validPosts.slice(0, 5).forEach((post, index) => {
    preview += `üìù POST ${index + 1} (${post.character_count} car.):\n`;
    preview += `${post.content}\n\n`;
    preview += `üí¨ ${post.comment}\n\n`;
    preview += `üè∑Ô∏è ${post.tags.join(' ')}\n\n‚∏ª\n\n`;
  });
} else {
  preview += `‚ùå Aucun post valide g√©n√©r√© sur ${allPosts.length} tentatives.\n\n`;
  if (invalidPosts.length > 0) {
    preview += `üìù Analyse des √©checs:\n`;

    // Show stats about rejections
    const tooShort = invalidPosts.filter(p => p.character_count < 75);
    const tooLong = invalidPosts.filter(p => p.character_count > 120);

    if (tooShort.length > 0) {
      preview += `üî∏ ${tooShort.length} posts trop courts (< 75 caract√®res)\n`;
      const avgShort = Math.round(tooShort.reduce((sum, p) => sum + p.character_count, 0) / tooShort.length);
      preview += `   Longueur moyenne: ${avgShort} caract√®res\n`;
    }
    if (tooLong.length > 0) {
      preview += `üî∏ ${tooLong.length} posts trop longs (> 120 caract√®res)\n`;
    }

    preview += `\nüìù Exemples de posts rejet√©s:\n`;
    invalidPosts.slice(0, 3).forEach((post, index) => {
      preview += `‚Ä¢ "${post.content.substring(0, 40)}..." - ${post.rejection_reason}\n`;
    });

    preview += `\nüí° L'IA g√©n√®re ${allPosts.length} posts mais aucun entre 75-120 caract√®res.\n`;
    preview += `üîß Solution: Reformulez votre demande avec plus de d√©tails et contexte.\n`;
  }
}

// Create unique session ID for this generation
const sessionId = 'session_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);

// Store posts data globally for later retrieval
$env.posts_data = {
  [sessionId]: {
    original_prompt: originalPrompt,
    posts: selectedPosts.length > 0 ? selectedPosts : validPosts.slice(0, 5),
    chat_id: $('Extract Message').item.json.chat_id,
    user_id: $('Extract Message').item.json.user_id,
    generation_timestamp: new Date().toISOString()
  }
};

return {
  session_id: sessionId,
  original_prompt: originalPrompt,
  ai_response: aiResponse,
  posts: selectedPosts.length > 0 ? selectedPosts : validPosts.slice(0, 5), // Fallback to valid posts
  all_generated_posts: allPosts,
  valid_posts: validPosts,
  invalid_posts: invalidPosts,
  posts_count: selectedPosts.length > 0 ? selectedPosts.length : Math.min(validPosts.length, 5),
  total_generated: allPosts.length,
  selection_stats: {
    generated: allPosts.length,
    valid: validPosts.length,
    selected: selectedPosts.length,
    rejected: invalidPosts.length,
    validation_criteria: '75-120 caract√®res'
  },
  preview_message: preview,
  chat_id: $('Extract Message').item.json.chat_id,
  user_id: $('Extract Message').item.json.user_id,
  generation_timestamp: new Date().toISOString()
};

// Process different callback actions
const callbackData = $input.item.json.callback_query?.data || '';
const chatId = $input.item.json.callback_query?.message?.chat?.id;
const userId = $input.item.json.callback_query?.from?.id;

// Extract session ID from callback data
const sessionIdMatch = callbackData.match(/_([^_]+)$/);
const sessionId = sessionIdMatch ? sessionIdMatch[1] : null;

// Determine action type
let action = 'unknown';
if (callbackData.startsWith('confirm_posts')) {
  action = 'confirm';
} else if (callbackData.startsWith('cancel_posts')) {
  action = 'cancel';
} else if (callbackData.startsWith('regenerate_posts')) {
  action = 'regenerate';
}

// Retrieve stored posts data
let postsData = null;
if (sessionId && $env.posts_data && $env.posts_data[sessionId]) {
  postsData = $env.posts_data[sessionId];
}

return {
  action: action,
  session_id: sessionId,
  chat_id: chatId,
  user_id: userId,
  callback_data: callbackData,
  posts: postsData?.posts || [],
  original_prompt: postsData?.original_prompt || '',
  action_timestamp: new Date().toISOString()
};

// Generate random posting schedule
const posts = $input.item.json.posts || [];
const now = new Date();
const scheduledPosts = [];

// Helper function to get random hour between ranges
function getRandomHour() {
  const morningHours = [7, 8, 9, 10];
  const afternoonHours = [12, 13, 14, 15];
  const eveningHours = [18, 19, 20, 21];

  const allHours = [...morningHours, ...afternoonHours, ...eveningHours];
  return allHours[Math.floor(Math.random() * allHours.length)];
}

// Helper function to get random future date (1-7 days)
function getRandomFutureDate() {
  const futureDate = new Date(now);
  futureDate.setDate(now.getDate() + Math.floor(Math.random() * 7) + 1);
  return futureDate;
}

// If no posts, return error
if (posts.length === 0) {
  throw new Error('Aucun post trouv√© pour la programmation');
}

posts.forEach((post, index) => {
  const postDate = getRandomFutureDate();
  postDate.setHours(getRandomHour(), Math.floor(Math.random() * 60), 0, 0);

  const generateId = () => {
    return 'POST_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
  };

  scheduledPosts.push({
    id: generateId(),
    is_posted: false,
    posted_on: null,
    scheduled_for: postDate.toISOString(),
    content: post.content,
    comment: post.comment,
    tags: Array.isArray(post.tags) ? post.tags.join(',') : post.tags || '',
    media: null,
    to_post_on: 'Facebook',
    content_type: 'Background Post',
    engagement_score: null,
    created_by: $input.item.json.user_id,
    created_at: now.toISOString(),
    last_updated: now.toISOString(),
    status: 'scheduled',
    topic: $input.item.json.original_prompt?.substring(0, 100) || 'General',
    character_count: post.content?.length || 0,
    priority: Math.floor(Math.random() * 3) + 1,
    target_audience: 'General',
    call_to_action: 'Engage',
    post_category: 'Motivational'
  });
});

// Clean up session data
if ($input.item.json.session_id && $env.posts_data) {
  delete $env.posts_data[$input.item.json.session_id];
}

return {
  scheduled_posts: scheduledPosts,
  total_posts: scheduledPosts.length,
  chat_id: $input.item.json.chat_id
};
