// ğŸ§  Enhanced Command Parser with Full Command System
const telegramData = $input.first().json;
const message = telegramData.message || {};
const messageText = message.text || '';
const userId = message.from?.id;
const chatId = message.chat?.id;
const messageId = message.message_id;
const files = message.document || message.photo || message.video || message.audio;
const timestamp = new Date().toISOString();

// ğŸ¯ Complete Command System from Documentation
const COMMANDS = {
  // Main Action Commands
  'BC': {
    action: 'background_content',
    description: 'Create a simple post with a colored background',
    transformer: 'text_transformer',
    postType: 'Background Colored Text Post',
    priority: 1
  },
  'BCW': {
    action: 'background_content_with_words',
    description: 'Create background content with additional comments',
    transformer: 'text_transformer',
    postType: 'Background Colored Text Post',
    priority: 2
  },
  'SC': {
    action: 'summary_content',
    description: 'Summarize the content provided',
    transformer: 'text_transformer',
    postType: 'Short Post',
    priority: 3
  },
  'BG': {
    action: 'background_generation',
    description: 'Create context or background information',
    transformer: 'text_transformer', 
    postType: 'Full Post',
    priority: 2
  },
  'CGO': {
    action: 'check_grammar_only',
    description: 'Verify grammar without altering meaning',
    transformer: 'text_transformer',
    postType: 'preserve_original',
    priority: 1
  },
  'MIB': {
    action: 'make_it_better',
    description: 'Improve structure, grammar, and readability',
    transformer: 'text_transformer',
    postType: 'Full Post',
    priority: 2
  },
  'PAI': {
    action: 'post_as_is',
    description: 'Post content exactly as provided',
    transformer: 'none',
    postType: 'preserve_original',
    priority: 1
  },
  'TL': {
    action: 'translate',
    description: 'Translate content into specified languages',
    transformer: 'text_transformer',
    postType: 'preserve_original',
    priority: 1
  },
  'KPC': {
    action: 'key_points_creation',
    description: 'Extract key points to create concise summaries',
    transformer: 'text_transformer',
    postType: 'Short Post',
    priority: 3
  },

  // Media-Specific Commands
  'IM': {
    action: 'include_media',
    description: 'Post content with attached media',
    transformer: 'media_processor',
    postType: 'Full Post',
    priority: 2
  },
  'KPS': {
    action: 'key_points_with_segments',
    description: 'Generate key points and video clips',
    transformer: 'video_transformer',
    postType: 'Short Post',
    priority: 4
  },
  'KPCV': {
    action: 'key_points_creation_video',
    description: 'Extract key points from video',
    transformer: 'video_transformer',
    postType: 'Short Post',
    priority: 3
  },
  'CVS': {
    action: 'create_video_segments',
    description: 'Divide video into segments',
    transformer: 'video_transformer',
    postType: 'Full Post',
    priority: 4
  },
  'IP': {
    action: 'image_processing',
    description: 'Analyze and summarize image context',
    transformer: 'image_transformer',
    postType: 'Short Post',
    priority: 2
  }
};

// ğŸŒ Platform Configuration
const PLATFORMS = {
  'LinkedIn': { 
    maxLength: 1300, tone: 'Professional', hashtagLimit: 5, 
    postTypes: ['Full Post', 'Short Post', 'Background Colored Text Post']
  },
  'Twitter': { 
    maxLength: 280, tone: 'Informal', hashtagLimit: 2,
    postTypes: ['Short Post', 'Background Colored Text Post']
  },
  'Facebook': { 
    maxLength: 2000, tone: 'Informal', hashtagLimit: 10,
    postTypes: ['Full Post', 'Short Post', 'Background Colored Text Post']
  },
  'Telegram': { 
    maxLength: 4096, tone: 'Informal', hashtagLimit: 5,
    postTypes: ['Full Post', 'Short Post']
  },
  'Instagram': { 
    maxLength: 2200, tone: 'Informal', hashtagLimit: 30,
    postTypes: ['Full Post', 'Background Colored Text Post']
  },
  'TikTok': { 
    maxLength: 150, tone: 'Informal', hashtagLimit: 5,
    postTypes: ['Short Post', 'Background Colored Text Post']
  }
};

// ğŸ¨ Tone Options
const TONE_OPTIONS = {
  'Amateur': 'Use simple, beginner-friendly language',
  'Professional': 'Use formal tone for workplace contexts',
  'Informal': 'Use casual conversational language'
};

// ğŸ” Advanced Command Detection
function parseCommand(text) {
  const upperText = text.toUpperCase();
  let detectedCommand = null;
  let parameters = {};

  // Check for explicit commands
  for (const [cmd, config] of Object.entries(COMMANDS)) {
    if (upperText.startsWith(cmd + ' ') || upperText === cmd) {
      detectedCommand = cmd;
      break;
    }
  }

  // Parse parameters
  const platformMatch = text.match(/Platform[:\s]+(.*?)(?=\s+[A-Z]+:|$)/i);
  if (platformMatch) {
    parameters.platforms = platformMatch[1].split(',').map(p => p.trim());
  }

  const languageMatch = text.match(/Language[:\s]+(.*?)(?=\s+[A-Z]+:|$)/i);
  if (languageMatch) {
    parameters.languages = languageMatch[1].split(',').map(l => l.trim());
  }

  const toneMatch = text.match(/Tone[:\s]+(\w+)/i);
  if (toneMatch) {
    parameters.tone = toneMatch[1];
  }

  const pageMatch = text.match(/Page[:\s]+(.*?)(?=\s+[A-Z]+:|$)/i);
  if (pageMatch) {
    parameters.pages = pageMatch[1].split(',').map(p => p.trim());
  }

  // Extract clean content (remove command and parameters)
  let cleanContent = text;
  if (detectedCommand) {
    cleanContent = text.replace(new RegExp(`^${detectedCommand}\\s*`, 'i'), '');
  }
  cleanContent = cleanContent
    .replace(/Platform[:\s]+[^\n]+/gi, '')
    .replace(/Language[:\s]+[^\n]+/gi, '')
    .replace(/Tone[:\s]+\w+/gi, '')
    .replace(/Page[:\s]+[^\n]+/gi, '')
    .trim();

  return {
    command: detectedCommand,
    parameters,
    cleanContent,
    hasExplicitCommand: !!detectedCommand
  };
}

// ğŸ¤– Content Type Detection
function detectContentType(message, files) {
  if (files) {
    if (message.document) {
      const mimeType = message.document.mime_type || '';
      if (mimeType.includes('pdf')) return 'pdf';
      if (mimeType.includes('word')) return 'document';
      return 'document';
    }
    if (message.photo) return 'image';
    if (message.video || message.video_note) return 'video';
    if (message.audio || message.voice) return 'audio';
  }

  const text = message.text || '';
  if (text.match(/https?:\/\/.+\.(jpg|jpeg|png|gif|webp)/i)) return 'image_url';
  if (text.match(/https?:\/\/.+\.(mp4|avi|mov|webm)/i)) return 'video_url';
  if (text.match(/https?:\/\/(www\.)?(youtube|youtu\.be|vimeo)/i)) return 'video_url';
  if (text.match(/https?:\/\//)) return 'url';

  return 'text';
}

// ğŸ§  Transformer Selection Logic
function selectTransformer(command, contentType, hasFiles) {
  if (command && COMMANDS[command]) {
    return COMMANDS[command].transformer;
  }

  // Auto-select based on content type
  if (contentType === 'video' || contentType === 'video_url') return 'video_transformer';
  if (contentType === 'image' || contentType === 'image_url') return 'image_transformer';
  if (contentType === 'pdf' || contentType === 'document') return 'document_transformer';
  if (hasFiles) return 'media_processor';

  return 'text_transformer';
}

// ğŸ“Š Quality Scoring
function calculateQualityScore(content, command, contentType) {
  let score = 50;

  // Content length
  if (content.length > 50 && content.length < 1000) score += 20;
  else if (content.length > 20) score += 10;

  // Command complexity
  if (command && COMMANDS[command]) {
    score += COMMANDS[command].priority * 5;
  }

  // Content type bonus
  if (contentType !== 'text') score += 10;

  // Quality indicators
  if (content.match(/[.!?]/g)?.length > 2) score += 10;
  if (content.includes('#')) score += 5;
  if (content.match(/https?:\/\//)) score += 10;

  return Math.min(100, Math.max(0, score));
}

// Generate unique message ID
const generateMessageId = () => {
  return `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
};

// Main processing
const uniqueMessageId = generateMessageId();
const commandParsing = parseCommand(messageText);
const contentType = detectContentType(message, files);
const selectedTransformer = selectTransformer(commandParsing.command, contentType, !!files);
const qualityScore = calculateQualityScore(commandParsing.cleanContent, commandParsing.command, contentType);

// Default platforms and languages
const defaultPlatforms = commandParsing.parameters.platforms || 
  (commandParsing.command === 'TL' ? ['LinkedIn', 'Facebook'] : ['Facebook', 'Twitter']);
const defaultLanguages = commandParsing.parameters.languages || ['French'];
const defaultTone = commandParsing.parameters.tone || 'Amateur';

// File information
let fileInfo = null;
if (files) {
  if (message.document) {
    fileInfo = {
      fileId: message.document.file_id,
      fileName: message.document.file_name,
      fileSize: message.document.file_size,
      mimeType: message.document.mime_type,
      type: 'document'
    };
  } else if (message.photo) {
    const photo = message.photo[message.photo.length - 1];
    fileInfo = {
      fileId: photo.file_id,
      fileName: `photo_${Date.now()}.jpg`,
      fileSize: photo.file_size,
      type: 'image'
    };
  } else if (message.video) {
    fileInfo = {
      fileId: message.video.file_id,
      fileName: `video_${Date.now()}.mp4`,
      fileSize: message.video.file_size,
      duration: message.video.duration,
      type: 'video'
    };
  }
}

return {
  // Core message data
  messageId: uniqueMessageId,
  telegramMessageId: messageId,
  userId,
  chatId,
  timestamp,

  // Content analysis
  originalContent: messageText,
  cleanContent: commandParsing.cleanContent,
  contentType,
  hasFiles: !!files,
  fileInfo,

  // Command system
  command: commandParsing.command,
  hasExplicitCommand: commandParsing.hasExplicitCommand,
  commandConfig: commandParsing.command ? COMMANDS[commandParsing.command] : null,
  parameters: commandParsing.parameters,

  // Processing configuration
  selectedTransformer,
  targetPlatforms: defaultPlatforms,
  languages: defaultLanguages,
  tone: defaultTone,

  // Quality and routing
  qualityScore,
  postType: commandParsing.command ? COMMANDS[commandParsing.command]?.postType : 'Full Post',

  // Agent decisions
  requiresTranslation: defaultLanguages.length > 1 || !defaultLanguages.includes('French'),
  requiresMediaProcessing: !!files,
  requiresUrlFetch: contentType === 'url' || contentType.includes('_url'),

  // Next action routing
  nextAction: {
    transformer: selectedTransformer,
    action: commandParsing.command ? COMMANDS[commandParsing.command]?.action : 'process_content',
    priority: commandParsing.command ? COMMANDS[commandParsing.command]?.priority : 2
  }
};

// ğŸ”„ Content Processor - Handles all transformer outputs
const originalData = $('Command Parser & Agent Brain').first().json;
const transformerResult = $input.first().json;

// Determine which transformer was used
const transformerUsed = originalData.selectedTransformer;
let processedContent = {};

// Process transformer-specific results
if (transformerUsed === 'text_transformer') {
  try {
    const aiResponse = transformerResult.choices[0].message.content;
    const parsedResult = JSON.parse(aiResponse);

    processedContent = {
      type: 'text_content',
      mainContent: parsedResult.processedContent || originalData.cleanContent,
      additionalComments: parsedResult.additionalComments || '',
      keyPoints: parsedResult.keyPoints || [],
      platformVersions: parsedResult.platformVersions || {},
      hashtags: parsedResult.hashtags || [],
      translatedVersions: parsedResult.translatedVersions || {},
      qualityScore: parsedResult.qualityScore || originalData.qualityScore
    };
  } catch (error) {
    // Fallback processing
    processedContent = {
      type: 'text_content',
      mainContent: originalData.cleanContent,
      additionalComments: '',
      keyPoints: [],
      platformVersions: {},
      hashtags: [],
      translatedVersions: {},
      qualityScore: originalData.qualityScore
    };
  }
} else if (transformerUsed === 'video_transformer') {
  try {
    const aiResponse = transformerResult.choices[0].message.content;
    const parsedResult = JSON.parse(aiResponse);

    processedContent = {
      type: 'video_content',
      transcription: parsedResult.transcription || '',
      segments: parsedResult.segments || [],
      videoSummary: parsedResult.videoSummary || '',
      keyPoints: parsedResult.keyPoints || [],
      hashtags: parsedResult.hashtags || [],
      qualityScore: parsedResult.qualityScore || originalData.qualityScore
    };
  } catch (error) {
    processedContent = {
      type: 'video_content',
      transcription: '',
      segments: [],
      videoSummary: originalData.cleanContent,
      keyPoints: [],
      hashtags: [],
      qualityScore: originalData.qualityScore
    };
  }
} else if (transformerUsed === 'image_transformer') {
  try {
    const aiResponse = transformerResult.choices[0].message.content;
    const parsedResult = JSON.parse(aiResponse);

    processedContent = {
      type: 'image_content',
      extractedText: parsedResult.extractedText || '',
      imageDescription: parsedResult.imageDescription || '',
      suggestedCaption: parsedResult.suggestedCaption || '',
      hashtags: parsedResult.hashtags || [],
      contentIdeas: parsedResult.contentIdeas || [],
      platformRecommendations: parsedResult.platformRecommendations || {},
      qualityScore: parsedResult.qualityScore || originalData.qualityScore
    };
  } catch (error) {
    processedContent = {
      type: 'image_content',
      extractedText: '',
      imageDescription: '',
      suggestedCaption: originalData.cleanContent,
      hashtags: [],
      contentIdeas: [],
      platformRecommendations: {},
      qualityScore: originalData.qualityScore
    };
  }
}

// Create platform-specific content
function createPlatformContent(content, platform, originalData) {
  const platformConfig = {
    'LinkedIn': { maxLength: 1300, tone: 'Professional' },
    'Twitter': { maxLength: 280, tone: 'Informal' },
    'Facebook': { maxLength: 2000, tone: 'Informal' },
    'Telegram': { maxLength: 4096, tone: 'Informal' },
    'Instagram': { maxLength: 2200, tone: 'Informal' },
    'TikTok': { maxLength: 150, tone: 'Informal' }
  };

  const config = platformConfig[platform] || platformConfig['Facebook'];
  let platformContent = '';

  if (content.type === 'text_content') {
    platformContent = content.platformVersions[platform] || content.mainContent;
    if (content.additionalComments && originalData.command === 'BCW') {
      platformContent += '\n\n' + content.additionalComments;
    }
  } else if (content.type === 'video_content') {
    platformContent = content.videoSummary;
    if (content.keyPoints.length > 0) {
      platformContent += '\n\nPoints clÃ©s:\n' + content.keyPoints.slice(0, 3).join('\n');
    }
  } else if (content.type === 'image_content') {
    platformContent = content.platformRecommendations[platform] || content.suggestedCaption;
  }

  // Ensure character limit
  if (platformContent.length > config.maxLength) {
    platformContent = platformContent.substring(0, config.maxLength - 3) + '...';
  }

  return platformContent;
}

// Create final platform-specific posts
const finalPosts = originalData.targetPlatforms.map(platform => {
  const platformContent = createPlatformContent(processedContent, platform, originalData);

  return {
    platform,
    content: platformContent,
    hashtags: processedContent.hashtags?.slice(0, 
      platform === 'Instagram' ? 30 : 
      platform === 'Facebook' ? 10 : 
      platform === 'LinkedIn' ? 5 : 2
    ) || [],
    readyForPosting: true
  };
});

// Handle translations if required
let translatedContent = {};
if (originalData.requiresTranslation && processedContent.translatedVersions) {
  translatedContent = processedContent.translatedVersions;
} else if (originalData.requiresTranslation) {
  // Create basic translations
  originalData.languages.forEach(lang => {
    translatedContent[lang] = processedContent.mainContent || processedContent.videoSummary || processedContent.suggestedCaption;
  });
}

return {
  // Original data
  ...originalData,

  // Processed content
  processedContent,
  transformerUsed,

  // Platform-specific posts
  finalPosts,
  translatedContent,

  // Final quality assessment
  finalQualityScore: processedContent.qualityScore,

  // Posting readiness
  readyForPosting: true,
  processingComplete: true,

  // Status tracking
  processingTimestamp: new Date().toISOString(),
  postingStatus: 'Ready for Posting'
};

// ğŸš€ Buffer Multi-Platform Poster
const contentData = $input.first().json;
const finalPosts = contentData.finalPosts || [];

// Buffer Profile Configuration
const BUFFER_PROFILES = {
  'LinkedIn': 'YOUR_LINKEDIN_PROFILE_ID',
  'Twitter': 'YOUR_TWITTER_PROFILE_ID', 
  'Facebook': 'YOUR_FACEBOOK_PROFILE_ID',
  'Instagram': 'YOUR_INSTAGRAM_PROFILE_ID',
  'TikTok': 'YOUR_TIKTOK_PROFILE_ID'
};

// Create Buffer posts for each platform
const bufferPosts = finalPosts.map(post => {
  const profileId = BUFFER_PROFILES[post.platform];

  if (!profileId || profileId.startsWith('YOUR_')) {
    return null; // Skip platforms without configured profiles
  }

  // Format content with hashtags
  let finalContent = post.content;
  if (post.hashtags && post.hashtags.length > 0) {
    finalContent += '\n\n' + post.hashtags.join(' ');
  }

  // Random scheduling (1-7 days from now)
  const now = new Date();
  const randomDays = Math.floor(Math.random() * 7) + 1;
  const randomHours = Math.floor(Math.random() * 12) + 8; // 8 AM to 8 PM
  const scheduledTime = new Date(now);
  scheduledTime.setDate(now.getDate() + randomDays);
  scheduledTime.setHours(randomHours, Math.floor(Math.random() * 60), 0, 0);

  return {
    text: finalContent,
    profile_ids: [profileId],
    scheduled_at: Math.floor(scheduledTime.getTime() / 1000), // Unix timestamp
    platform: post.platform,
    originalMessageId: contentData.messageId,
    commandUsed: contentData.command,
    scheduledDateTime: scheduledTime.toISOString()
  };
}).filter(post => post !== null);

return {
  ...contentData,
  bufferPosts,
  totalPostsToSchedule: bufferPosts.length,
  platformsToPost: bufferPosts.map(p => p.platform),
  readyForBuffer: bufferPosts.length > 0
};

// Split buffer posts for individual API calls
const postData = $input.first().json;
return postData.bufferPosts.map(bufferPost => ({
  ...postData,
  currentBufferPost: bufferPost
}));

